755→    <!-- ========== JavaScript ========== -->
756→    <script>
757→        // --- Theme ---
758→        function getStoredTheme() {
759→            try { return localStorage.getItem('df-theme'); } catch { return null; }
760→        }
761→        function setTheme(theme) {
762→            document.documentElement.setAttribute('data-theme', theme);
763→            try { localStorage.setItem('df-theme', theme); } catch {}
764→        }
765→        function toggleTheme() {
766→            const current = document.documentElement.getAttribute('data-theme');
767→            setTheme(current === 'dark' ? 'light' : 'dark');
768→        }
769→        // Init: respect stored preference, else system preference, else dark
770→        (function() {
771→            const stored = getStoredTheme();
772→            if (stored) { setTheme(stored); return; }
773→            if (window.matchMedia && window.matchMedia('(prefers-color-scheme: light)').matches) {
774→                setTheme('light');
775→            }
776→        })();
777→
778→        // --- Version tabs ---
779→        function switchVersion(version) {
780→            document.querySelectorAll('.version-panel').forEach(p => p.classList.remove('active'));
781→            document.querySelectorAll('.tab-btn').forEach(t => t.classList.remove('active'));
782→            document.getElementById('version-' + version).classList.add('active');
783→            document.getElementById('tab-' + version).classList.add('active');
784→            document.getElementById('variant-field').value = version.toUpperCase();
785→        }
786→
787→        // --- Waitlist form ---
788→        const form = document.getElementById('waitlist-form');
789→        const success = document.getElementById('waitlist-success');
790→        form.addEventListener('submit', async (e) => {
791→            e.preventDefault();
792→            const data = new FormData(form);
793→            try {
794→                const res = await fetch(form.action, {
795→                    method: 'POST', body: data,
796→                    headers: { 'Accept': 'application/json' }
797→                });
798→                if (res.ok) {
799→                    form.classList.add('hidden');
800→                    success.classList.remove('hidden');
801→                }
802→            } catch { form.submit(); }
803→        });
804→    </script>
