 939→        // --- Theme ---
 940→        function getStoredTheme() {
 941→            try { return localStorage.getItem('df-theme'); } catch { return null; }
 942→        }
 943→        function setTheme(theme) {
 944→            document.documentElement.setAttribute('data-theme', theme);
 945→            try { localStorage.setItem('df-theme', theme); } catch {}
 946→        }
 947→        function toggleTheme() {
 948→            const current = document.documentElement.getAttribute('data-theme');
 949→            setTheme(current === 'dark' ? 'light' : 'dark');
 950→        }
 951→        // Init: respect stored preference, else system, else light (default)
 952→        (function() {
 953→            const stored = getStoredTheme();
 954→            if (stored) { setTheme(stored); return; }
 955→            if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
 956→                setTheme('dark');
 957→            }
 958→            // else default is light (set in HTML)
 959→        })();
 960→
 961→        // Keyboard support for theme toggle
 962→        document.querySelector('.theme-toggle').addEventListener('keydown', function(e) {
 963→            if (e.key === 'Enter' || e.key === ' ') {
 964→                e.preventDefault();
 965→                toggleTheme();
 966→            }
 967→        });
 968→
 969→        // --- Smooth scroll for anchor links ---
 970→        document.querySelectorAll('a[href^="#"]').forEach(link => {
 971→            link.addEventListener('click', function(e) {
 972→                const target = document.querySelector(this.getAttribute('href'));
 973→                if (target) {
 974→                    e.preventDefault();
 975→                    target.scrollIntoView({ behavior: 'smooth', block: 'start' });
 976→                }
 977→            });
 978→        });
 979→
 980→        // --- Waitlist form ---
 981→        const form = document.getElementById('waitlist-form');
 982→        const successEl = document.getElementById('waitlist-success');
 983→        form.addEventListener('submit', async function(e) {
 984→            e.preventDefault();
 985→            const data = new FormData(form);
 986→            try {
 987→                const res = await fetch(form.action, {
 988→                    method: 'POST',
 989→                    body: data,
 990→                    headers: { 'Accept': 'application/json' }
 991→                });
 992→                if (res.ok) {
 993→                    form.style.display = 'none';
 994→                    successEl.classList.add('visible');
 995→                }
 996→            } catch {
 997→                // Fallback: native submit
 998→                form.submit();
 999→            }
1000→        });
1001→    </script>
