  24→            --surface-solid: #FFFFFF;
  25→            --accent: #E8788A;
  26→            --accent-hover: #D4556A;
  27→            --accent-rgb: 232, 120, 138;
  28→            --mint: #7ECFB3;
  29→            --lavender: #B8A9E8;
  30→            --peach: #FFB088;
  31→            --sky: #8AC4F4;
  32→            --text-primary: #2D1F2D;
  33→            --text-secondary: #7A6B7A;
  34→            --border: rgba(0, 0, 0, 0.06);
  35→            --glass-bg: rgba(255, 255, 255, 0.55);
  36→            --glass-blur: 20px;
  37→            --glass-border: rgba(0, 0, 0, 0.06);
  38→            --glass-shadow: 0 4px 24px rgba(45, 31, 45, 0.06);
  39→            --glass-elevated-blur: 28px;
  40→            --glass-elevated-shadow: 0 8px 40px rgba(45, 31, 45, 0.08), 0 0 60px rgba(232, 120, 138, 0.06);
  41→            --nav-bg: rgba(255, 245, 247, 0.7);
  42→            --input-bg: rgba(255, 255, 255, 0.8);
  43→            --pixel-dot: #E8788A;
  44→        }
  45→
  46→        [data-theme="dark"] {
  47→            --bg: #1A1118;
  48→            --surface: rgba(30, 20, 28, 0.7);
  49→            --surface-solid: #241C22;
  50→            --accent: #E8788A;
  51→            --accent-hover: #F09DAA;
  52→            --accent-rgb: 232, 120, 138;
  53→            --text-primary: #F5EEF2;
  54→            --text-secondary: #A89BA6;
  55→            --border: rgba(255, 255, 255, 0.08);
  56→            --glass-bg: rgba(30, 20, 28, 0.55);
  57→            --glass-border: rgba(255, 255, 255, 0.08);
  58→            --glass-shadow: 0 4px 24px rgba(0, 0, 0, 0.2);
  59→            --glass-elevated-shadow: 0 8px 40px rgba(0, 0, 0, 0.3), 0 0 60px rgba(232, 120, 138, 0.08);
  60→            --nav-bg: rgba(26, 17, 24, 0.75);
  61→            --input-bg: rgba(36, 28, 34, 0.8);
  62→        }
  63→
  64→        /* ===== Base ===== */
  65→        body {
  66→            font-family: var(--font-body);
  67→            background: var(--bg);
  68→            color: var(--text-primary);
  69→            line-height: 1.6;
  70→            transition: background 0.35s ease, color 0.35s ease;
  71→            overflow-x: hidden;
  72→        }
  73→
  74→        h1, h2, h3 { font-family: var(--font-headline); font-weight: 400; }
  75→
  76→        a { text-decoration: none; color: inherit; cursor: pointer; }
  77→
  78→        img { max-width: 100%; height: auto; }
  79→
  80→        /* ===== Glassmorphism ===== */
  81→        .glass {
  82→            background: var(--glass-bg);
  83→            backdrop-filter: blur(var(--glass-blur));
  84→            -webkit-backdrop-filter: blur(var(--glass-blur));
  85→            border: 1px solid var(--glass-border);
  86→            box-shadow: var(--glass-shadow);
  87→        }
  88→
  89→        .glass-elevated {
  90→            background: var(--glass-bg);
  91→            backdrop-filter: blur(var(--glass-elevated-blur));
  92→            -webkit-backdrop-filter: blur(var(--glass-elevated-blur));
  93→            border: 1px solid var(--glass-border);
  94→            box-shadow: var(--glass-elevated-shadow);
  95→        }
  96→
  97→        /* ===== Buttons ===== */
  98→        .btn-primary {
  99→            display: inline-flex;
 100→            align-items: center;
 101→            justify-content: center;
 102→            gap: 0.5rem;
 103→            padding: 1rem 2rem;
 104→            background: var(--accent);
 105→            color: #fff;
 106→            font-family: var(--font-body);
 107→            font-weight: 600;
 108→            font-size: 1rem;
 109→            border: none;
 110→            border-radius: 1rem;
 111→            cursor: pointer;
 112→            transition: all 0.2s ease;
 113→        }
 114→        .btn-primary:hover {
 115→            background: var(--accent-hover);
 116→            transform: translateY(-1px);
 117→            box-shadow: 0 8px 30px rgba(var(--accent-rgb), 0.3);
 118→        }
 119→
 120→        .btn-ghost {
 121→            display: inline-flex;
 122→            align-items: center;
 123→            justify-content: center;
 124→            gap: 0.5rem;
 125→            padding: 1rem 2rem;
 126→            background: transparent;
 127→            color: var(--accent);
 128→            font-family: var(--font-body);
 129→            font-weight: 600;
 130→            font-size: 1rem;
 131→            border: 2px solid var(--accent);
 132→            border-radius: 1rem;
 133→            cursor: pointer;
 134→            transition: all 0.2s ease;
 135→        }
 136→        .btn-ghost:hover {
 137→            background: rgba(var(--accent-rgb), 0.08);
 138→            transform: translateY(-1px);
 139→        }
 140→
 141→        /* ===== Navbar ===== */
