 198→        /* Theme toggle */
 199→        .theme-toggle {
 200→            width: 44px;
 201→            height: 26px;
 202→            border-radius: 13px;
 203→            background: rgba(var(--accent-rgb), 0.15);
 204→            border: 1px solid rgba(var(--accent-rgb), 0.25);
 205→            position: relative;
 206→            cursor: pointer;
 207→            transition: background 0.3s;
 208→            flex-shrink: 0;
 209→        }
 210→        .theme-toggle::after {
 211→            content: '';
 212→            position: absolute;
 213→            width: 20px;
 214→            height: 20px;
 215→            border-radius: 50%;
 216→            background: var(--accent);
 217→            top: 2px;
 218→            left: 2px;
 219→            transition: transform 0.3s;
 220→            box-shadow: 0 1px 4px rgba(0,0,0,0.15);
 221→        }
 222→        [data-theme="dark"] .theme-toggle::after {
 223→            transform: translateX(18px);
 224→        }
 225→        .theme-icon {
 226→            position: absolute;
 227→            top: 50%;
 228→            transform: translateY(-50%);
 229→            font-size: 12px;
 230→            line-height: 1;
 231→            pointer-events: none;
 232→        }
 233→        .theme-icon--sun { left: 5px; }
 234→        .theme-icon--moon { right: 5px; }
 235→        [data-theme="light"] .theme-icon--moon { opacity: 0.3; }
 236→        [data-theme="dark"] .theme-icon--sun { opacity: 0.3; }
