 631→        /* ===== Pixel Corner Decorations ===== */
 632→        .pixel-corner {
 633→            position: absolute;
 634→            width: 24px;
 635→            height: 24px;
 636→            opacity: 0.3;
 637→            pointer-events: none;
 638→        }
 639→        .pixel-corner--tl { top: 8px; left: 8px; }
 640→        .pixel-corner--tr { top: 8px; right: 8px; transform: scaleX(-1); }
 641→        .pixel-corner--bl { bottom: 8px; left: 8px; transform: scaleY(-1); }
 642→        .pixel-corner--br { bottom: 8px; right: 8px; transform: scale(-1); }
 643→        .pixel-corner svg { width: 100%; height: 100%; }
 644→
 645→        /* ===== Reduced Motion ===== */
 646→        @media (prefers-reduced-motion: reduce) {
 647→            *, *::before, *::after {
 648→                animation-duration: 0.01ms !important;
 649→                transition-duration: 0.01ms !important;
 650→            }
 651→        }
 652→
 653→        /* ===== Responsive ===== */
 654→        @media (max-width: 1024px) {
 655→            .hero {
 656→                grid-template-columns: 1fr;
 657→                gap: 3rem;
